import React, { useEffect, useState } from 'react';
import { db, auth } from './firebase.js';
import { collection, getDocs, addDoc } from 'firebase/firestore';

function CoinCollection() {
  const [coins, setCoins] = useState([]);
  const [newCoin, setNewCoin] = useState({ country: '', year: '', mint: '', note: '' });
  const [loading, setLoading] = useState(false);
  const user = auth.currentUser;

  // Fetch existing coins
  const fetchCoins = async () => {
    if (user) {
      const querySnapshot = await getDocs(collection(db, 'users', user.uid, 'coins'));
      const fetchedCoins = querySnapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
      setCoins(fetchedCoins);
    }
  };

  useEffect(() => {
    fetchCoins();
  }, [user]);

  // Handle form inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewCoin({ ...newCoin, [name]: value });
  };

  // Add a new coin to Firestore
  const handleAddCoin = async (e) => {
    e.preventDefault();
    setLoading(true);

    if (user) {
      const coinCollectionRef = collection(db, 'users', user.uid, 'coins');
      try {
        await addDoc(coinCollectionRef, newCoin);
        setNewCoin({ country: '', year: '', mint: '', note: '' });
        await fetchCoins();
      } catch (error) {
        console.error('Error adding coin:', error);
      } finally {
        setLoading(false);
      }
    }
  };

  return (
    <div className="collection-container">
      <h2>Your Coin Collection</h2>
      <form onSubmit={handleAddCoin}>
        <div>
          <label>Country:</label>
          <input
            type="text"
            name="country"
            value={newCoin.country}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Year:</label>
          <input
            type="text"
            name="year"
            value={newCoin.year}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Mint:</label>
          <input
            type="text"
            name="mint"
            value={newCoin.mint}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Note:</label>
          <textarea
            name="note"
            value={newCoin.note}
            onChange={handleChange}
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Adding...' : 'Add Coin'}
        </button>
      </form>

      {coins.length > 0 ? (
        <ul>
          {coins.map((coin) => (
            <li key={coin.id}>
              <strong>Country:</strong> {coin.country || 'N/A'}, 
              <strong> Year:</strong> {coin.year || 'N/A'}, 
              <strong> Mint:</strong> {coin.mint || 'N/A'}
              <br />
              <em>Note:</em> {coin.note || 'No note'}
            </li>
          ))}
        </ul>
      ) : (
        <p>No coins in your collection.</p>
      )}
    </div>
  );
}

export default CoinCollection;
