import React, { useState } from 'react';
import { auth, createUserWithEmailAndPassword, signInWithEmailAndPassword } from './firebaseConfig';
import './App.css';

function App() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);

  // Handle file input change
  const handleFileChange = (event) => {
    const files = Array.from(event.target.files);
    if (selectedFiles.length + files.length > 2) {
      alert("You can only upload a maximum of 2 files.");
      return;
    }
    setSelectedFiles((prevFiles) => [...prevFiles, ...files]);
  };

  // Handle form submission
  const handleSubmit = async (event) => {
    event.preventDefault();
    if (selectedFiles.length === 0) {
      alert("Please select image files first.");
      return;
    }
    setLoading(true);
    try {
      const formData = new FormData();
      selectedFiles.forEach((file) => formData.append("files", file));

      const res = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error("Failed to upload files");
      const data = await res.json();
      setResponse(data);
    } catch (error) {
      console.error("Error:", error);
      setResponse({ error: "An error occurred while processing the images." });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Coiny</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={handleFileChange} accept="image/*" multiple />
        <button type="submit" disabled={loading}>
          {loading ? "Processing..." : "Upload Images"}
        </button>
      </form>

      {response && (
        <table>
          <tbody>
            <tr>
              <td>Country</td>
              <td>{response.country}</td>
            </tr>
            <tr>
              <td>Year</td>
              <td>{response.year}</td>
            </tr>
            <tr>
              <td>Mint</td>
              <td>{response.mint}</td>
            </tr>
            <tr>
              <td>Denomination</td>
              <td>{response.denomination}</td>
            </tr>
            <tr>
              <td>Estimated Price</td>
              <td>{response.estimatedPrice}</td>
            </tr>
            <tr>
              <td>Fun Fact</td>
              <td>{response.funFact}</td>
            </tr>
          </tbody>
        </table>
      )}
    </div>
  );
}

export default App;
