import React, { useState } from 'react';
import './App.css';

function App() {
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);

  // Handle file input change
  const handleFileChange = (event) => {
    const files = Array.from(event.target.files);
    // Limit the total number of files to 2
    if (selectedFiles.length + files.length > 2) {
      alert("You can only upload a maximum of 2 files.");
      return;
    }
    setSelectedFiles(prevFiles => [...prevFiles, ...files]);
  };

  // Handle file removal
  const handleRemoveFile = (index) => {
    setSelectedFiles((prevFiles) => prevFiles.filter((_, i) => i !== index));
  };

  // Handle form submission to upload files
  const handleSubmit = async (event) => {
    event.preventDefault();

    if (selectedFiles.length === 0) {
      alert("Please select image files first.");
      return;
    }

    setLoading(true);
    setResponse(null); // Clear previous response

    try {
      const formData = new FormData();
      selectedFiles.forEach(file => formData.append("files", file));

      // Send a POST request to the server
      const res = await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      if (!res.ok) throw new Error("Failed to upload files");

      const data = await res.json();
      setResponse(data);
    } catch (error) {
      console.error("Error uploading files:", error);
      setResponse({ error: "An error occurred while processing the images." });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Coiny</h1>
        <p>Upload images of coins to retrieve information</p>
        
        {/* Form to upload files */}
        <form onSubmit={handleSubmit}>
          <div className="file-input-container">
            <label htmlFor="file-upload" className="custom-file-button">
              Choose Files
            </label>
            <input
              type="file"
              id="file-upload"
              onChange={handleFileChange}
              accept="image/*"
              multiple
            />
          </div>

          {/* Display chosen file names and previews */}
          <div className="image-preview-container">
            {selectedFiles.map((file, index) => (
              <div key={index} className="image-preview">
                <img src={URL.createObjectURL(file)} alt={`Preview ${index}`} />
                <button
                  type="button"
                  className="remove-button"
                  onClick={() => handleRemoveFile(index)}
                >
                  &times;
                </button>
              </div>
            ))}
          </div>

          {/* Upload button */}
          <button type="submit" disabled={loading || selectedFiles.length === 0}>
            {loading ? "Processing..." : "Upload Images"}
          </button>
        </form>

        {/* Display response from server */}
        {response && (
          <div className="table-container">
            {response.error ? (
              <p className="error-message">{response.error}</p>
            ) : (
              <table className="styled-table">
                <thead>
                  <tr>
                    <th>Field</th>
                    <th>Value</th>
                  </tr>
                </thead>
                <tbody>
                  <tr>
                    <td>Country</td>
                    <td>{response.country || 'N/A'}</td>
                  </tr>
                  <tr>
                    <td>Year</td>
                    <td>{response.year || 'N/A'}</td>
                  </tr>
                  <tr>
                    <td>Mint</td>
                    <td>{response.mint || 'N/A'}</td>
                  </tr>
                  <tr>
                    <td>Denomination</td>
                    <td>{response.denomination || 'N/A'}</td>
                  </tr>
                  <tr>
                    <td>Estimated Price</td>
                    <td>{response.estimatedPrice || 'N/A'}</td>
                  </tr>
                  <tr>
                    <td>Fun Fact</td>
                    <td>{response.funFact || 'N/A'}</td>
                  </tr>
                </tbody>
              </table>
            )}
          </div>
        )}
      </header>
    </div>
  );
}

export default App;
